import java.io.File;import net.dromard.filesynchronizer.FileSynchronizerVisitor;import net.dromard.filesynchronizer.gui.MainFrame;import net.dromard.filesynchronizer.treenode.FileSynchronizerTodoTaskTreeNode;/** * FileSynchronizerVisitor is the program that aim is to backup files by synchronising two folders. * If the destination folder dos not exists than a simple copy will be done, else a  * synchronisation will be done using source as master. *  * @author Gabriel Dromard */public class FileSynchronizer {	/**	 * The start point of the program.	 * Usage:	 *		usage: FileSynchronizerVisitor [Source Folder] [Destination Folder] [Mode]	 *	 examples: FileSynchronizerVisitor ./MyDocs /MyHDD/Backup	 *	 * The Modes define the level of output from 0 (nothing) to 8 (all)	 * @param args	 */	public static void main(String[] args) {		try {			FileSynchronizerTodoTaskTreeNode backup = null;			FileSynchronizerVisitor visitor = null;			boolean process = false;						if (args.length > 0 && (args[0].equals("-h") || args[0].equals("--help") || args[0].equals("/?"))) {				printUsage();			} else if (args.length == 0 || args.length == 2) {				MainFrame.main(args);				return;			} else if (args.length == 3) {				backup = new FileSynchronizerTodoTaskTreeNode(new File(args[0]), new File(args[1]));				visitor = new FileSynchronizerVisitor(Integer.parseInt(args[2]), process);			} else if (args.length == 4) {				process = Boolean.parseBoolean(args[3]);				backup = new FileSynchronizerTodoTaskTreeNode(new File(args[0]), new File(args[1]));				visitor = new FileSynchronizerVisitor(Integer.parseInt(args[2]), process);			}			if (backup != null) {				visitor.visit(backup);				visitor.printSummary();			} else {				printUsage();			}		} catch (Exception e) {			e.printStackTrace();		}	}	private static void printUsage() {		System.out.println("usage: FileSynchronizer [Source Folder] [Destination Folder] [level] [process (true/false)]");		System.out.println("ex: FileSynchronizer									  # Run UI");		System.out.println("ex: FileSynchronizer [Source Folder] [Destination Folder] # Run UI with preloading");	}}